mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.RNUM as num, tt1.category_no as "categoryNo", TT1.category_nm as "categoryNm", TT1.top_no as "topNo", -- 당일이면 시:분, 그렇지 않으면 년-월-일 DECODE( TO_CHAR(SYSDATE,'YYYY-MM-DD'), TO_CHAR(TT1.reg_dt,'YYYY-MM-DD') ,TO_CHAR(TT1.reg_dt,'HH24:MI') ,TO_CHAR(TT1.reg_dt,'YYYY-MM-DD')) as regDt, DECODE( TO_CHAR(SYSDATE,'YYYY-MM-DD'), TO_CHAR(TT1.mod_dt,'YYYY-MM-DD') ,TO_CHAR(TT1.mod_dt,'HH24:MI') ,TO_CHAR(TT1.mod_dt,'YYYY-MM-DD')) as modDt, TT1.status FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM category -- WHERE 조건 (검색구분:''(전체), 10(category_nm), 20(status), 검색어)
ORDER BY reg_dt ) T1 -- 종료 번호 WHERE ROWNUM <= #{pageSize} * (#{pageNo} - 1) + #{pageSize} ) TT1 -- 시작번호 WHERE ROWNUM >= #{pageSize} * (#{pageNo} - 1) + 1 ) A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM category --WHERE 조건
) B

mapper.insert:
INSERT INTO category (
  category_no,
  category_nm,
  top_no
) VALUES (
  #{categoryNo},
  #{categoryNm},
  #{topNo}
)
