mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.seq as seq, TT1.gubun as "gubun", TT1.title as "title", TT1.contents as "contents", -- 당일이면 시:분, 그렇지 않으면 년-월-일 DECODE( TO_CHAR(SYSDATE,'YYYY-MM-DD'), TO_CHAR(TT1.reg_dt,'YYYY-MM-DD') ,TO_CHAR(TT1.reg_dt,'HH24:MI') ,TO_CHAR(TT1.reg_dt,'YYYY-MM-DD')) as regDt, TT1.reg_id FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM board
ORDER BY reg_dt DESC ) T1 -- 종료 번호 WHERE ROWNUM <= #{pageSize} * (#{pageNo} - 1) + #{pageSize} ) TT1 -- 시작번호 WHERE ROWNUM >= #{pageSize} * (#{pageNo} - 1) + 1 ) A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM board --WHERE 조건
) B

mapper.update:
UPDATE board
SET   title = #{title}
    , contents = #{contents}
    , reg_dt = SYSDATE
WHERE seq = #{seq}
