mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.rnum AS num, TT1.item_no AS itemNo, CASE WHEN LENGTH(TT1.name) >= 12 THEN SUBSTR(TT1.name, 0, 10) || '..' ELSE TT1.name END AS name, --TT1.name, TT1.price, TT1.image_no AS imageNo, TT1.save_name AS saveName, TT1.view_path AS viewPath FROM ( SELECT ROWNUM AS RNUM,T1.* FROM ( SELECT p.*, i.save_name, i.view_path FROM product p, image i WHERE p.image_no = i.image_no --조인 조건
ORDER BY p.reg_dt DESC )T1 --종료 번호 WHERE ROWNUM <= #{pageSize} * (#{pageNo} - 1) + #{pageSize} )TT1 --시작번호 WHERE RNUM >= #{pageSize} * (#{pageNo} - 1)+1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM product p, image i WHERE p.image_no = i.image_no --조인 조건
)B
